package csd.starter;

public class Location {
	private int x;
	
	private int y;  
	public Location(int x2, int y2) {
		// TODO Auto-generated constructor stub
		this.x = x2;
		this.y = y2;
	}
	public int getX() {
		return x;
	}
	public void setX(int x) {
		this.x = x;
	}
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	
}
